mapper.select:

select p.id,p.name,p.cover,oitem.quantity from t_product p LEFT JOIN (select o.id as
order_id,oi.product_id,oi.quantity from t_order o,t_order_item oi where oi.order_id=o.id and o.created_date
between (select STR_TO_DATE(VALUE,'%Y-%m-%d') from t_config_field where field='starting_time') and
DATE_ADD((select STR_TO_DATE(VALUE,'%Y-%m-%d') from t_config_field where field='starting_time')
,INTERVAL (select value from t_config_field where field='settlement_cycle' ) MONTH)
) as oitem ON p.id=oitem.product_id GROUP BY p.id ORDER BY oitem.quantity desc limit #{off_set},#{pageSize}
